var __hasOwn = Object.prototype.hasOwnProperty;

exports.extend = function(a,b) {
	for (k in b) {
		a[k] = b[k];
	}
	return a;
}

exports.fillVariables = function (text, variables) {
	var newText = text;
	for (var prop in variables) {
		newText = newText.replace("{" + prop + "}", variables[prop]);
	}
	return newText;
}

